void   TClasParticle::SetId(int id)
{
  TParticlePDG *ptr_PART = TDatabasePDG::Instance()->GetParticle(id);
  // if Particle not found in the database
  if(ptr_PART==NULL)
    {
      printf("TClasParticle::ERROR: Particle with %d id is not found\n ",id);
      pdgID = 0;
      return;
    }
  pdgID   = id;
  pdgMass = ptr_PART->Mass();
}

void   TClasParticle::SetPartById(int id, TVector3 p3, TVector3 v3)
{
   TParticlePDG *ptr_PART = TDatabasePDG::Instance()->GetParticle(id);
  // if Particle not found in the database
  if(ptr_PART==NULL)
    {
      printf("TClasParticle::ERROR: Particle with %d id is not found\n ",id);
      pdgID = 0;
      return;
    }

  pdgID = id;
  pdgMass = ptr_PART->Mass();
  fPartVector.SetVectM(p3,pdgMass);
  fPartVertex = v3;
}

void   TClasParticle::Reset()
{
  pdgID   = 0;
  pdgMass = 0.;
  tofMass = 0.;
  fPartVector.SetPxPyPzE(0.,0.,0.,0.);
  fPartVertex.SetXYZ(0.,0.,0.);
  partCharge = 0;
  SetStatus(-1);
}
