TClasParticle &TClasParticle::operator=(const TClasParticle &part)
{ 
  return *this;
}

string  TClasParticle::particleToStringLund()
{
  stringstream strOut;
  strOut << setw(7)  << GetPdgId();
  strOut << setw(12) << setprecision(5) << pdgMass;
  strOut << setw(12) << setprecision(5) << fPartVector.Px();
  strOut << setw(12) << setprecision(5) << fPartVector.Py();
  strOut << setw(12) << setprecision(5) << fPartVector.Pz();
  strOut << setw(12) << setprecision(5) << fPartVertex.X();
  strOut << setw(12) << setprecision(5) << fPartVertex.Y();
  strOut << setw(12) << setprecision(5) << fPartVertex.Z();
  return strOut.str();
}

TString          TClasParticle::ToString()
{
  TString res = particleToStringLund().c_str();
  return res;
}
