int part_index(int ipdg)
{
  int index = -1;
  for (unsigned int it=0; it<part_ipdg.size(); it++)
  {
    if(ipdg == part_ipdg.at(it) ) {index = it; break;}
  }

  if(index == -1 || index >= (int)part_ipdg.size())
  {
    cout <<"error indx larger than vector size "<< part_ipdg.size() <<endl;
  }

  return index;
}
